package uz.gita.jaxongir.sellermanageruser.ui.componentsimport androidx.compose.foundation.BorderStrokeimport androidx.compose.foundation.backgroundimport androidx.compose.foundation.borderimport androidx.compose.foundation.layout.*import androidx.compose.material3.ExperimentalMaterial3Apiimport androidx.compose.material3.MaterialThemeimport androidx.compose.material3.Textimport androidx.compose.material3.TextFieldimport androidx.compose.runtime.*import androidx.compose.ui.Modifierimport androidx.compose.ui.graphics.Colorimport androidx.compose.ui.graphics.Shapeimport androidx.compose.ui.unit.dp@Composablefun SimpleSearchItem(    modifier: Modifier = Modifier,    value: String,    onValueChange: (String) -> Unit,    placeholder: String = "Search",    shape: Shape = MaterialTheme.shapes.small,) {    var isSearching by remember { mutableStateOf(false) }    Box(        modifier = modifier            .fillMaxWidth()            .background(                color = Color(0xFFA3C0F1),                shape = shape            )    ) {        TextField(            value = value,            onValueChange = {                onValueChange(it)                isSearching = it.isNotEmpty()            },            textStyle = MaterialTheme.typography.bodySmall.copy(color = Color.Black),            placeholder = { Text(text = placeholder, color = Color.Gray) },            singleLine = true,            modifier = Modifier                .fillMaxWidth()                .background(                    color = Color(0xFF673AB7),                )                .border(BorderStroke(1.dp, Color(0xFF212835)))                .padding(16.dp)        )    }}